mapper.delete:
DELETE FROM BOARD_IMG 
WHERE BOARD_NO = #{boardNo}	
AND BOARDT_DEL_FL IN ('Y', 'T')

mapper.update:
UPDATE BOARD SET
BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD SET BOARD_DEL_FL = 'N'
WHERE BOARD_NO = #{boardNo}
AND BOARD_DEL_FL = 'T'
