mapper.select:
  	SELECT COUNT(*) FROM "MEMBER" m
	WHERE MEMBER_DEL_FL IN ('N','S')
	AND MEMBER_LEVEL_NO = #{memberLevelNoResult}

SELECT COUNT(*) FROM (SELECT COMMENT_COUNT, MEMBER_NO ,MEMBER_LEVEL_NO FROM (SELECT NVL(COMMENT_COUNT,0) COMMENT_COUNT, MEMBER_NO ,MEMBER_LEVEL_NO FROM "MEMBER" m LEFT JOIN (SELECT COUNT(COMMENT_NO) COMMENT_COUNT,MEMBER_NO FROM "COMMENT" c
WHERE to_char(C_CREATE_DATE,'yyyy.mm.dd') BETWEEN to_char(add_months(sysdate,-1),'yyyy.mm.dd') AND to_char(SYSDATE,'yyyy.mm.dd')
GROUP BY MEMBER_NO ORDER BY 2) USING(MEMBER_NO) WHERE MEMBER_DEL_FL IN ('N','S'))
COMMENT_COUNT >= #{commentCountInput}
COMMENT_COUNT <= #{commentCountInput}
AND MEMBER_LEVEL_NO = #{memberLevelNo}
)

SELECT * FROM (SELECT PROFILE_IMG, MEMBER_NICKNAME, SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) MEMBER_EMAIL, TO_CHAR(ENROLL_DATE ,'YYYY.MM.DD') ENROLL_DATE, (SELECT MEMBER_LEVEL_IMG FROM MEMBER_LEVEL ml WHERE ml.MEMBER_LEVEL_NO = m.MEMBER_LEVEL_NO) MEMBER_LEVEL_IMG, (SELECT MEMBER_LEVEL_NAME FROM MEMBER_LEVEL ml WHERE ml.MEMBER_LEVEL_NO = m.MEMBER_LEVEL_NO) MEMBER_LEVEL_NAME ,MEMBER_LEVEL_NO ,(SELECT AUTHORITY_IMG FROM AUTHORITY a WHERE a.AUTHORITY_NO = m.AUTHORITY_NO) AUTHORITY_IMG ,(SELECT AUTHORITY_NAME FROM AUTHORITY a WHERE a.AUTHORITY_NO = m.AUTHORITY_NO) AUTHORITY_NAME ,TO_CHAR(LOGIN_DATE ,'YYYY.MM.DD') LOGIN_DATE ,NVL(LOGHISTORY_COUNT,0) LOGHISTORY_COUNT ,NVL(BOARD_COUNT,0) BOARD_COUNT ,NVL(COMMENT_COUNT,0) COMMENT_COUNT FROM "MEMBER" m LEFT JOIN (SELECT MAX(LOGIN_DATE) LOGIN_DATE,MEMBER_NO ,COUNT(LOGIN_DATE) LOGHISTORY_COUNT FROM LOGIN_HISTORY lh GROUP BY MEMBER_NO ORDER BY 2) USING(MEMBER_NO) LEFT JOIN (SELECT COUNT(BOARD_NO) BOARD_COUNT ,MEMBER_NO FROM BOARD b GROUP BY MEMBER_NO ORDER BY 2) USING(MEMBER_NO) LEFT JOIN (SELECT COUNT(COMMENT_NO) COMMENT_COUNT,MEMBER_NO FROM "COMMENT" c
WHERE to_char(C_CREATE_DATE,'yyyy.mm.dd') BETWEEN to_char(add_months(sysdate,-1),'yyyy.mm.dd') AND to_char(SYSDATE,'yyyy.mm.dd')
GROUP BY MEMBER_NO ORDER BY 2) USING(MEMBER_NO) WHERE MEMBER_DEL_FL IN ('N','Y') ORDER BY MEMBER_NICKNAME)
COMMENT_COUNT >= #{commentCountInput}
COMMENT_COUNT <= #{commentCountInput}
AND MEMBER_LEVEL_NO = #{memberLevelNo}
