update.selectKey:
SELECT COUNT(*)
FROM "MEMBER" 
WHERE MEMBER_LEVEL_NO =  #{memberLevelNo} 
AND SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) IN 
#{memberEmail}
AND AUTHORITY_NO NOT IN (0,1)

update.foreach:
        UPDATE "MEMBER" SET 
			MEMBER_LEVEL_NO = #{memberLevelNo} 
			WHERE SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) = #{memberEmail}
			AND AUTHORITY_NO NOT IN (0,1)

mapper.insert:
INSERT INTO MEMBER_HOLD   
	SELECT SEQ_HOLD_NO.NEXTVAL, 
  	H.* 
 	, #{HmemberNo} ,SYSDATE ,'Y' , #{comment} 
 	FROM

			SELECT MEMBER_NO FROM "MEMBER" WHERE SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) = #{memberEmail}

mapper.update:
UPDATE "MEMBER" SET MEMBER_DEL_FL = 'S' WHERE SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) IN
#{memberEmail}
AND AUTHORITY_NO NOT IN (0,1)

mapper.insert:
		INSERT INTO MEMBER_SECESSION   
		SELECT SEQ_SECESSION_NO.NEXTVAL, 
  	SYSDATE , #{secessionreason} ,
 		S.* , #{SmemberNo} , #{comment} 
		 	FROM

					SELECT MEMBER_NO FROM "MEMBER" WHERE SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) = #{memberEmail}

mapper.update:
UPDATE "MEMBER" SET MEMBER_DEL_FL = 'Y' WHERE SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) IN
#{memberEmail}
AND AUTHORITY_NO NOT IN (0)

UPDATE "MEMBER" SET MEMBER_DEL_FL = 'N' WHERE SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) IN
#{memberEmail}
AND MEMBER_DEL_FL = 'S'

UPDATE "MEMBER_SECESSION" SET SECESSION_REASON = 'I' WHERE S_MEMBER_NO IN (SELECT MEMBER_NO FROM "MEMBER" WHERE SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) IN
#{memberEmail}
) AND S_MANAGER_NO IS NOT NULL

UPDATE "MEMBER_SECESSION" SET SECESSION_REASON = 'F' WHERE S_MEMBER_NO IN (SELECT MEMBER_NO FROM "MEMBER" WHERE SUBSTR(MEMBER_EMAIL, 0, INSTR(MEMBER_EMAIL, '@')-1) IN
#{memberEmail}
) AND S_MANAGER_NO IS NOT NULL
