mapper.update:
 	UPDATE "MEMBER" SET
 	MEMBER_NICKNAME = #{memberNickname},
	MEMBER_RESIDENCE = #{memberResidence}
 	WHERE MEMBER_NO =#{memberNo}

mapper.select:
SELECT COUNT(*) FROM "BOARD"
WHERE MEMBER_NO =#{memberNo}
AND BOARD_DEL_FL = 'N'

SELECT COUNT(DISTINCT BOARD_NO) FROM "COMMENT"
WHERE MEMBER_NO =#{memberNo}
AND COMMENT_DEL_FL= 'N'

SELECT COUNT(*) FROM "BOARD"
WHERE MEMBER_NO =#{memberNo}
AND BOARD_DEL_FL = 'Y'
